import React, { Component } from "react";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { temp: 0, desc: "", icon: "", loading: true };
  }
  componenentDidMount() {
    fetch(
      "http://api.openweathermap.org/data/2.5/weather?q=Helsinki&units=Metric&APIkey=6fd138d7824444ac43192603e7cd897c"
    )
      .then(response => response.json())
      .then(responseData => {
        this.setState({
          temp: responseData.main.temp,
          desc: responseData.weather[0].description,
          icon: responseData.weather[0].icon,
          loading: false
        });
      });
  }
  render() {
    const imgSrc = `http://openweathermap.org/img/w/${this.state.icon}.png`;
    return (
      <div className="App">
        <p>Temprature: {this.state.temp}</p>
        <p>Description: {this.state.desc}</p>
        <img src={imgSrc} alt="weather icon" />
      </div>
    );
  }
}
export default App;
